ruleExplore.bookList:
+<js>let json=JSON.parse(result).data;
let list=json.list||json;
list.map(bk=>{
let bid=bk.bookId||bk.id;
return {
a:bk.name,
b:bk.author,
c:bk.categoryName,
d:bk.wordNum,
e:bk.chapterName,
f:bk.intro,
g:bk.picture,
h:"http://statics.rungean.com/static/book/zip/"+parseInt((1999+bid)/2000)+"/"+bid+".zip,{'type':'zip'}"
};
})
</js>

ruleSearch.bookList:
+<js>let json=JSON.parse(result).data;
let list=json.list||json;
list.map(bk=>{
let bid=bk.bookId||bk.id;
return {
a:bk.name,
b:bk.author,
c:bk.chapterUpdateTime+'·'+bk.categoryName,
d:bk.wordNum,
e:bk.chapterName,
f:bk.intro,
g:bk.picture,
h:"http://statics.rungean.com/static/book/zip/"+parseInt((1999+bid)/2000)+"/"+bid+".zip,{'type':'zip','User-Agent':'okhttp/3.14.24'}"
};
})
</js>

