@js:
var html=result,
options={headers:{Referer:baseUrl}};

(function getImgList() {
    eval(html.match(/(var qTcms_Cur=[\S\s]+var qTcms_S_show_1=.*?;)/)[1]);
    var list = java.base64Decode(qTcms_S_m_murl_e).split("\\$qingtiandy\\$");
    var piclist = new Array();
    for (var i = 0; i < list.length; i++) {
        var v = list[i];
        var s = v;
        if (v.substring(0, 1) == "/") {
            s = qTcms_m_weburl + s;
        } else {
            if (qTcms_Pic_m_if != "2") {
                v = v.replaceAll("\\?", "a1a1");
                v = v.replaceAll("&", "b1b1");
                v = v.replaceAll("%", "c1c1");
                var m_httpurl = "";
                if (typeof(qTcms_S_m_mhttpurl) != "undefined") m_httpurl = java.base64Decode(qTcms_S_m_mhttpurl);
            }
        }
        piclist[i] =s;
    }
    return piclist;
}()).map(uri=>
'<img src="'+uri+'">').join("\n")

ruleSearch.bookList:
data.books[*]
@js:JSON.parse(result).map(o=> {
    var J = org.jsoup.Jsoup.parse(java.ajax(baseUrl.match(/(.+?\/\/.+?\/)/)[1] + 'source/' + o.sourceUuid));
    return {
        name: o.title,
        author: o.author,
        tags: o.keyName,
        num:o.showTotalCount+'字',
        latest: String(J.select('.m-book-recent a').text()).replace(/[\(（【].*[求更谢乐发推].*[】）\)]/,'') +String(J.select('.m-book-recent p').text()).replace(/\s*(\d+-\d+-\d+).+/, '（$1）').replace(/\s*(\d+)月(\d+)日.+/, '（$1-$2）'),
        intro: o.description.replace(/<\/?\w+>/g, '').trim(),
        cover: o.coverImage,
        catalogUrl: '/reader/book/info.json?catalog_only=true&source_uuid=' + o.sourceUuid
    }
});

